import { useState, useEffect } from "react";
import ExamList from "./ExamList";
import useFetch from "./useFetch";
import useAuth from "./hooks/useAuth";

const Home = () => {
  const { auth } = useAuth();

  const [activeExams, setActiveAExams] = useState(null);
  const [outdatedExams, setOutdatedExams] = useState(null);

  useEffect(() => {
    const IsActive = async (userID, examID) => {
      const { data } = await useFetch(
        "http://localhost:3500/exam/student/studentexam/find/" +
          userID +
          "/" +
          examID
      );
      return data;
    };

    const getExams = async () => {
      const {
        data: userExams,
        isPending,
        error,
      } = await useFetch("http://localhost:3500/exam/student/" + auth?.userID);
      return userExams;
    };

    setActiveAExams(
      userExams &&
        userExams.filter((exam) => {
          console.log(exam);
          let examDate = Date.parse(exam.date);
          if (examDate > currentDate && IsActive(auth.userID, exam._id) == 1) {
            return true;
          } else {
            return false;
          }
        })
    );
    console.log(userExams);

