    //             if (ue._id == exam._id) {
    //               return true;
    //             }
    //             return false;
    //           });
    //           let examDate = Date.parse(exam.date);
    //           if (examDate < currentDate || userExam[0].active == 0) {
    //             return true;
    //           } else {
    //             return false;
    //           }
    //         })
    //     );
  }, []);

  const currentDate = Date.now();

  return (
    <div className="home">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {userExams && (
        <ExamList
          exams={activeExams}
          title="Active Exams"
          classType="activeExamList"
        />
      )}
      {userExams && (
        <ExamList
          exams={outdatedExams}
          title="Outdated Exams"
          classType="outdatedExamList"
        />
      )}
    </div>
  );
};

export default Home;
