import { useNavigate, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const Question = ({ question }) => {
  return (
    <div className="exam-frame">
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {questions &&
        questions.map((question) => (
          <div className="questionFrame">
            <h4>Question {questionNumber}</h4>
            <p>{question.question}</p>
            <form action="">
              <div className="option">
                <input
                  type="radio"
                  name="options"
                  id="optionA"
                  value={"option1"}
                />
                <label htmlFor="optionA"> A) {question.optionA}</label>
              </div>

              <div className="option">
                <input
                  type="radio"
                  name="options"
                  id="optionB"
                  value={"optionB"}
                />
                <label htmlFor="optionB"> B) {question.optionB}</label>
              </div>
              <div className="option">
                <input
                  type="radio"
                  name="options"
                  id="optionC"
                  value={"optionC"}
                />
                <label htmlFor="optionC"> C) {question.optionC}</label>
              </div>
              <div className="option">
                <input
                  type="radio"
                  name="options"
                  id="optionD"
                  value={"optionD"}
                />
                <label htmlFor="optionD"> D) {question.optionD}</label>
              </div>
            </form>
          </div>
        ))}
    </div>
  );
};

export default Question;
