  return (
    <div className="home">
      {/* {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>} */}
      {
        <ExamList
          exams={activeExams}
          title="Active Exams"
          classType="activeExamList"
        />
      }
      {
        <ExamList
          exams={outdatedExams}
          title="Outdated Exams"
          classType="outdatedExamList"
        />
      }
    </div>
  );
};

export default Home;
