  return (
    <div className="create">
      <h1>Add a New Quiz</h1>
      <form action="">
        <label>Quiz Title:</label>
        <input
          type="text"
          required
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label>Quiz Date:</label>
        <input
          type="date"
          required
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label>Duration Minutes:</label>
        <input
          type="number"
          min="0"
          step="1"
          required
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </form>
    </div>
  );
};

export default Create;
