import { useNavigate, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const Exam = () => {
  const { id, question } = useParams();
  const {
    data: questions,
    isPending,
    error,
  } = useFetch(
    "http://localhost:8000/questions?examID=" + id + "&id=" + question
  );

  return (
    <div className="exam-frame">
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {questions &&
        questions.map((question) => (
          <div className="questionFrame">
            <p>{question.question}</p>
            <form action="">
              <input type="radio" id="option1" value={"option1"} />
            </form>
          </div>
        ))}
    </div>
  );
};

export default Exam;
