import { useState, useEffect } from "react";
import ExamList from "./ExamList";
import useFetch from "./useFetch";
//import useAuth from "./hooks/useAuth";
//import axios from "./api/axios";
const Home = () => {
  //const { auth } = useAuth();

  const [activeExams, setActiveAExams] = useState(null);
  const [outdatedExams, setOutdatedExams] = useState(null);

  const {
    data: userExams,
    isPending,
    error,
  } = useFetch("http://localhost:3500/exam/student/" + auth?.userID);
  const IsActive = async (userID, examID) => {
    const { data } = await useFetch(
      "http://localhost:3500/exam/student/studentexam/find/" +
        userID +
        "/" +
        examID
    );
    return data;
  };

  useEffect(() => {
    userExams &&
      setActiveAExams(
        userExams &&
          userExams.filter((exam) => {
            console.log(exam);
            let examDate = Date.parse(exam.date);
            if (
              examDate > currentDate &&
              IsActive(auth.userID, exam._id).active == 1
            ) {
              return true;
            } else {
              return false;
            }
          })
      );

    exams &&
      setOutdatedExams(
        userExams &&
          userExams.filter((exam) => {
            const userExam = userExams.filter((ue) => {
              if (ue._id == exam._id) {
                return true;
              }
              return false;
            });
            let examDate = Date.parse(exam.date);
            if (examDate < currentDate || userExam[0].active == 0) {
              return true;
            } else {
              return false;
            }
          })
      );
  });

  const currentDate = Date.now();

  return (
    <div className="home">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {
        <ExamList
          exams={activeExams}
          title="Active Exams"
          classType="activeExamList"
        />
      }
      {
        <ExamList
          exams={outdatedExams}
          title="Outdated Exams"
          classType="outdatedExamList"
        />
      }
    </div>
  );
};

export default Home;
