  //     navigator("/");
  //   });
  // };

  return (
    <div className="blog-details">
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {blog && (
        <article>
          <h2>{exam.examName}</h2>
          <p>Start Date {exam.startDate}</p>
          <div>{blog.body}</div>
          {/* <button onClick={handleClick}>delete</button> */}
        </article>
      )}
    </div>
  );
};

export default BlogDetails;
