import { useState, useEffect } from "react";
import ExamList from "./ExamList";
import useFetch from "./useFetch";
import useAuth from "./hooks/useAuth";
import axios from "./api/axios";

const Home = () => {
  const { auth } = useAuth();
  const {
    data: exams,
    isPending,
    error,
  } = useFetch("http://localhost:3500/exam/active/663243238dfa9308588591f6");

  const {
    data: userExams,
    isPendings,
    errors,
  } = useFetch("http://localhost:3500/exam/outdated/663243238dfa9308588591f6");

  const [activeExams, setActiveAExams] = useState(null);
  const [outdatedExams, setOutdatedExams] = useState(null);

  useEffect(() => {
    exams && setActiveAExams(exams);

    userExams && setOutdatedExams(userExams);
  });

  return (
    <div className="home">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {errors && <div>{errors}</div>}
      {isPendings && <div>Loading...</div>}

      {exams && (
        <ExamList
          exams={activeExams}
          title="Active Exams"
          classType="activeExamList"
        />
      )}
      {exams && (
        <ExamList
          exams={outdatedExams}
          title="Outdated Exams"
          classType="outdatedExamList"
        />
      )}
    </div>
  );
};

export default Home;
