import { useCookies } from "react-cookie";
import { Link } from "react-router-dom";

const Welcome = () => {
  const [cookies, setCookie, removeCookie] = useCookies(["userID"]);
  return (
    <div>
      <h1> WELCOME TO EXAM.COM, DEAR {cookies?.name.toUpperCase()} !</h1>
      {cookies.role === "student" ? (
        <h3>
          {" "}
          Let's check your <Link to="/exams">EXAMS</Link>{" "}
        </h3>
      ) : null}
      {cookies.role === "teacher" ? (
        <h3 style={{ margin: 10 }}>
          {" "}
          Let's check your <Link to="/teacherexams">EXAMS</Link> or Create a new{" "}
          <Link to="create">EXAM</Link>{" "}
        </h3>
      ) : null}
    </div>
  );
};

export default Welcome;
