import Question from "./Question";
import { useParams } from "react-router-dom";
import useFetch from "./useFetch";
import { useState } from "react";

const Exam = () => {
  const { id } = useParams();
  const [questionNumber, setQuestionNumber] = useState(1);
  const [answers, setAnswers] = useState("");
  const [answer, setAnswer] = useState("");

  const {
    data: question,
    isPending,
    error,
  } = useFetch(
    "http://localhost:8000/questions?examID=" + id + "&id=" + questionNumber
  );

  const {
    data: exam,
    isPendingExam,
    errorExam,
  } = useFetch("http://localhost:8000/exams/" + id);

  const handleClick = (e) => {
    e.preventDefault();
    setAnswers(answers + answer);
    setQuestionNumber(questionNumber + 1);
  };

  if (exam.questionNumber < this.questionNumber) {
    return (
      <div className="exam-finsihed">
        <h1>Completed The Exam</h1>
      </div>
    );
  }

  return (
    <div className="exam-frame">
      {answers}
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {question && (
        <div className="exam-frame">
          {
            <div className="questionFrame">
              <h4>Question {questionNumber}</h4>
              <p>{question[0].question}</p>
              <form onSubmit={handleClick}>
                <div className="option">
                  <input
                    type="radio"
                    name="options"
                    id="optionA"
                    value={"A"}
                    onChange={(e) => setAnswer(e.target.value)}
                  />
                  <label htmlFor="optionA"> A) {question[0].optionA}</label>
                </div>

                <div className="option">
                  <input
                    type="radio"
                    name="options"
                    id="optionB"
                    value={"B"}
                    onChange={(e) => setAnswer(e.target.value)}
                  />
                  <label htmlFor="optionB"> B) {question[0].optionB}</label>
                </div>
                <div className="option">
                  <input
                    type="radio"
                    name="options"
                    id="optionC"
                    value={"C"}
                    onChange={(e) => setAnswer(e.target.value)}
                  />
                  <label htmlFor="optionC"> C) {question[0].optionC}</label>
                </div>
                <div className="option">
                  <input
                    type="radio"
                    name="options"
                    id="optionD"
                    value={"D"}
                    onChange={(e) => setAnswer(answers + e.target.value)}
                  />
                  <label htmlFor="optionD"> D) {question[0].optionD}</label>
                </div>
                <button type="submit"> Next Question</button>
              </form>
            </div>
          }
        </div>
      )}
    </div>
  );
};

export default Exam;
