import { useNavigate, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const Exam = () => {
  const { id, questionNumber } = useParams();
  const {
    data: questions,
    isPending,
    error,
  } = useFetch(
    "http://localhost:8000/questions?examID=" + id + "&id=" + questionNumber
  );

  return (
    <div className="exam-frame">
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {questions &&
        questions.map((question) => (
          <div className="questionFrame">
            <h2>Question {questionNumber}</h2>
            <p>{question.question}</p>
            <form action="">
              <div className="option">
                <input
                  type="radio"
                  name="options"
                  id="optionA"
                  value={"option1"}
                />
                <label htmlFor="optionA"> A) {question.optionA}</label>
              </div>

              <div className="option">
                <input
                  type="radio"
                  name="options"
                  id="optionB"
                  value={"optionB"}
                />
                <label htmlFor="optionB"> B) {question.optionB}</label>
              </div>
              <div className="option">
                <input
                  type="radio"
                  name="options"
                  id="optionC"
                  value={"optionC"}
                />
                <label htmlFor="optionC"> C) {question.optionC}</label>
              </div>
              <div className="option">
                <input
                  type="radio"
                  name="options"
                  id="optionD"
                  value={"optionD"}
                />
                <label htmlFor="optionD"> D) {question.optionD}</label>
              </div>
            </form>
          </div>
        ))}
    </div>
  );
};

export default Exam;
