import { useState, useEffect } from "react";
import BlogList from "./BlogList";
import useFetch from "./useFetch";

const Home = () => {
  const [activeExams, setActiveAExams] = useState(null);
  const [outdatedExams, setOutdatedExams] = useState(null);

  useEffect(() => {
    const {
      data: exams,
      isPending,
      error,
    } = useFetch("http://localhost:8000/exams");

    setActiveAExams(
      exams.filter((exam) => {
        let examDate = Date.parse(exam.StartDate);
        console.log(examDate);
        if (examDate > currentDate) {
          return true;
        } else {
          return false;
        }
      })
    );

    setOutdatedExams(
      exams.filter((exam) => {
        let examDate = Date.parse("05 Mar 2024 00:00:00 GMT");
        console.log(examDate);
        if (examDate < currentDate) {
          return true;
        } else {
          return false;
        }
      })
    );
  });

  const currentDate = Date.now();

  return (
    <div className="home">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {exams && <BlogList exams={activeExams} title="Active Exams" />}
      {exams &&
        exams
          .filter((exam) => {
            let examDate = Date.parse("05 Mar 2024 00:00:00 GMT");
            console.log(examDate);
            if (examDate < currentDate) {
              return true;
            } else {
              return false;
            }
          })
          .map((filteredExam) => (
            <BlogList exams={exams} title="Outdated Exams" />
          ))}
    </div>
  );
};

export default Home;
