import { useNavigate, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const Exam = () => {
  const { id, question } = useParams();
  const {
    data: questions,
    isPending,
    error,
  } = useFetch(
    "http://localhost:8000/questions?examID=" + id + "&id=" + question
  );

  return (
    <div className="exam-frame">
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {questions &&
        questions.map((question) => (
          <div className="questionFrame">
            <p>{question.question}</p>
            <form action="">
              <div className="option">
                <input
                  type="radio"
                  name="options"
                  id="optionA"
                  value={"option1"}
                />
                <label htmlFor="optionA"> A) {question.optionA}</label>
              </div>
              <input
                type="radio"
                name="options"
                id="optionB"
                value={"optionB"}
              />
              <label htmlFor="optionB"> B) {question.optionB}</label>
              <input
                type="radio"
                name="options"
                id="optionC"
                value={"optionC"}
              />
              <label htmlFor="optionC"> C) {question.optionC}</label>
              <input
                type="radio"
                name="options"
                id="optionD"
                value={"optionD"}
              />
              <label htmlFor="optionD"> D) {question.optionD}</label>
            </form>
          </div>
        ))}
    </div>
  );
};

export default Exam;
