import { useNavigate, useParams } from "react-router-dom";
import useFetch from "./useFetch";
import { useState } from "react";
import axios from "axios";

const BlogDetails = () => {
  const { id } = useParams();
  const [wait, setWait] = useState(true);
  const {
    data: exam,
    isPending,
    error,
  } = useFetch("http://localhost:8000/exams/" + id);
  const navigator = useNavigate();
  const handleClick = () => {
    navigator("/exam/" + id);
  };

  async function isExamStarted() {
    const globalDate = await axios.get(
      "https://timeapi.io/api/Time/current/zone?timeZone=Europe/Istanbul"
    );
    const currentDate = globalDate.data.dateTime;
    if (currentDate >= exam.startDate) {
      console.log("OÇ HALİL");
      setWait(false);
    }
  }

  useFetch(() => {
    exam && isExamStarted();
  }, []);

  return (
    <div className="blog-details">
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {exam && (
        <article>
          <h2>{exam.examName}</h2>
          <p>Start Date: {exam.startDate}</p>
          <p>Start Date: {exam.duartion}</p>

          <div>{exam.questionNumber}</div>
          {!wait && <button onClick={handleClick}>JOIN</button>}
          {wait && (
            <button disabled style={{ background: "#383838" }}>
              JOIN
            </button>
          )}
        </article>
      )}
    </div>
  );
};

export default BlogDetails;
