import { useState, useEffect } from "react";
import ExamList from "./ExamList";
import useFetch from "./useFetch";

const Home = () => {
  const {
    data: exams,
    isPending,
    error,
  } = useFetch("http://localhost:8000/exams");

  const {
    data: userExams,
    isPendings,
    errors,
  } = useFetch("http://localhost:8000/exams");

  const [activeExams, setActiveAExams] = useState(null);
  const [outdatedExams, setOutdatedExams] = useState(null);

  useEffect(() => {
    exams &&
      setActiveAExams(
        exams.filter((exam) => {
          userExam = userExams.filter((ue) => ue.examID === exam.id);
          let examDate = Date.parse(exam.startDate);
          if (examDate > currentDate && userExam.active) {
            return true;
          } else {
            return false;
          }
        })
      );

    setOutdatedExams(
      exams &&
        exams.filter((exam) => {
          let examDate = Date.parse(exam.startDate);
          if (examDate < currentDate) {
            return true;
          } else {
            return false;
          }
        })
    );
  });

  const currentDate = Date.now();

  return (
    <div className="home">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {exams && (
        <ExamList
          exams={activeExams}
          title="Active Exams"
          classType="activeExamList"
        />
      )}
      {exams && (
        <ExamList
          exams={outdatedExams}
          title="Outdated Exams"
          classType="outdatedExamList"
        />
      )}
    </div>
  );
};

export default Home;
